"use strict";(self.webpackChunkageyev_github_io=self.webpackChunkageyev_github_io||[]).push([[66],{5353:e=>{e.exports=JSON.parse('{"archive":{"blogPosts":[{"id":"from_the_Nile_to_the_Euphrates","metadata":{"permalink":"/uk/blog/from_the_Nile_to_the_Euphrates","source":"@site/i18n/uk/docusaurus-plugin-content-blog/2025-02-06-from_the_Nile_to_the_Euphrates/index.md","title":"On the Biblical Borders of Israel","description":"\u041e\u0434\u043d\u0438\u043c \u0456\u0437 \u0434\u0438\u0441\u043a\u0443\u0441\u0456\u0439\u043d\u0438\u0445 \u043f\u0438\u0442\u0430\u043d\u044c \u043f\u0440\u0438 \u043e\u0431\u0433\u043e\u0432\u043e\u0440\u0435\u043d\u043d\u0456 \u043a\u043e\u0440\u0434\u043e\u043d\u0456\u0432 \u0406\u0437\u0440\u0430\u0457\u043b\u044e \u0437 \u0442\u043e\u0447\u043a\u0438 \u0437\u043e\u0440\u0443 \u043f\u0440\u0430\u0432\u0430 (\u043d\u0430 \u043d\u0430\u0448 \u043f\u043e\u0433\u043b\u044f\u0434, \u0441\u0432\u044f\u0449\u0435\u043d\u043d\u0456 \u0442\u0435\u043a\u0441\u0442\u0438 \u0442\u0430\u043a\u043e\u0436 \u043f\u043e\u0432\u0438\u043d\u043d\u0456 \u0440\u043e\u0437\u0433\u043b\u044f\u0434\u0430\u0442\u0438\u0441\u044f \u0432 \u043c\u0456\u0436\u043d\u0430\u0440\u043e\u0434\u043d\u043e\u043c\u0443 \u043f\u0440\u0430\u0432\u0456) \u0454 \u0432\u0456\u0434\u043e\u043c\u0438\u0439 \u0432\u0438\u0441\u043b\u0456\u0432 \\"\u0432\u0456\u0434 \u041d\u0456\u043b\u0443 \u0434\u043e \u0404\u0432\u0444\u0440\u0430\u0442\u0443\\", \u044f\u043a\u0438\u0439 \u0440\u043e\u0437\u0443\u043c\u0456\u0454\u0442\u044c\u0441\u044f \u044f\u043a \u0442\u0430\u043d\u0430\u0445\u0456\u0447\u043d\u0435 (\u0431\u0456\u0431\u043b\u0456\u0439\u043d\u0435) \u0432\u0438\u0437\u043d\u0430\u0447\u0435\u043d\u043d\u044f \u043a\u043e\u0440\u0434\u043e\u043d\u0456\u0432 \u0406\u0437\u0440\u0430\u0457\u043b\u044e.","date":"2025-02-06T00:00:00.000Z","tags":[{"inline":false,"label":"\u0412\u0456\u043a\u0442\u043e\u0440 \u0410\u0433\u0454\u0454\u0432","permalink":"/uk/blog/tags/ageyev","description":"\u0412\u0456\u043a\u0442\u043e\u0440 \u0410\u0433\u0454\u0454\u0432"},{"inline":false,"label":"\u0406\u0437\u0440\u0430\u0457\u043b\u044c","permalink":"/uk/blog/tags/israel","description":"\u0406\u0437\u0440\u0430\u0457\u043b\u044c"}],"hasTruncateMarker":true,"authors":[{"name":"Viktor Ageyev","title":"author","page":{"permalink":"/uk/blog/authors/ageyev"},"socials":{"x":"https://x.com/ageyev","linkedin":"https://www.linkedin.com/in/ageyev/","github":"https://github.com/ageyev"},"imageURL":"/uk/img/authors/ageyev_viktor_02.jpg","key":"ageyev"}],"frontMatter":{"slug":"from_the_Nile_to_the_Euphrates","title":"On the Biblical Borders of Israel","authors":["ageyev"],"tags":["ageyev","israel"],"draft":false},"unlisted":false,"nextItem":{"title":"7 \u0436\u043e\u0432\u0442\u043d\u044f","permalink":"/uk/blog/october7"}},"content":"\u041e\u0434\u043d\u0438\u043c \u0456\u0437 \u0434\u0438\u0441\u043a\u0443\u0441\u0456\u0439\u043d\u0438\u0445 \u043f\u0438\u0442\u0430\u043d\u044c \u043f\u0440\u0438 \u043e\u0431\u0433\u043e\u0432\u043e\u0440\u0435\u043d\u043d\u0456 \u043a\u043e\u0440\u0434\u043e\u043d\u0456\u0432 \u0406\u0437\u0440\u0430\u0457\u043b\u044e \u0437 \u0442\u043e\u0447\u043a\u0438 \u0437\u043e\u0440\u0443 \u043f\u0440\u0430\u0432\u0430 (\u043d\u0430 \u043d\u0430\u0448 \u043f\u043e\u0433\u043b\u044f\u0434, \u0441\u0432\u044f\u0449\u0435\u043d\u043d\u0456 \u0442\u0435\u043a\u0441\u0442\u0438 \u0442\u0430\u043a\u043e\u0436 \u043f\u043e\u0432\u0438\u043d\u043d\u0456 \u0440\u043e\u0437\u0433\u043b\u044f\u0434\u0430\u0442\u0438\u0441\u044f \u0432 \u043c\u0456\u0436\u043d\u0430\u0440\u043e\u0434\u043d\u043e\u043c\u0443 \u043f\u0440\u0430\u0432\u0456) \u0454 \u0432\u0456\u0434\u043e\u043c\u0438\u0439 \u0432\u0438\u0441\u043b\u0456\u0432 *\\"\u0432\u0456\u0434 \u041d\u0456\u043b\u0443 \u0434\u043e \u0404\u0432\u0444\u0440\u0430\u0442\u0443\\"*, \u044f\u043a\u0438\u0439 \u0440\u043e\u0437\u0443\u043c\u0456\u0454\u0442\u044c\u0441\u044f \u044f\u043a \u0442\u0430\u043d\u0430\u0445\u0456\u0447\u043d\u0435 (\u0431\u0456\u0431\u043b\u0456\u0439\u043d\u0435) \u0432\u0438\u0437\u043d\u0430\u0447\u0435\u043d\u043d\u044f \u043a\u043e\u0440\u0434\u043e\u043d\u0456\u0432 \u0406\u0437\u0440\u0430\u0457\u043b\u044e.\\n\\n\u0412\u0456\u0434\u043f\u043e\u0432\u0456\u0434\u043d\u043e, \u0437 \u043e\u0434\u043d\u043e\u0433\u043e \u0431\u043e\u043a\u0443, \u0434\u0435\u044f\u043a\u0456 \u0456\u0437\u0440\u0430\u0457\u043b\u044c\u0442\u044f\u043d\u0438 \u0432\u0432\u0430\u0436\u0430\u044e\u0442\u044c, \u0449\u043e \u043a\u043e\u0440\u0434\u043e\u043d\u0438 \u0406\u0437\u0440\u0430\u0457\u043b\u044e \u0441\u043b\u0456\u0434 \u0440\u043e\u0437\u0448\u0438\u0440\u0438\u0442\u0438 \u0434\u043e \\"\u0432\u0441\u0442\u0430\u043d\u043e\u0432\u043b\u0435\u043d\u0438\u0445 \u0422\u043e\u0440\u043e\u044e\\" \u0432\u0456\u0434 \u041d\u0456\u043b\u0443 \u0434\u043e \u0404\u0432\u0444\u0440\u0430\u0442\u0443. \u0417 \u0456\u043d\u0448\u043e\u0433\u043e \u0431\u043e\u043a\u0443, \u043f\u0440\u043e\u0442\u0438\u0432\u043d\u0438\u043a\u0438 \u0406\u0437\u0440\u0430\u0457\u043b\u044e \u0437\u0432\u0438\u043d\u0443\u0432\u0430\u0447\u0443\u044e\u0442\u044c \u0439\u043e\u0433\u043e \u0443 \u043f\u0440\u0430\u0433\u043d\u0435\u043d\u043d\u0456 \u0437\u0430\u0445\u043e\u043f\u0438\u0442\u0438 \u0432\u0456\u0434\u043f\u043e\u0432\u0456\u0434\u043d\u0456 \u0442\u0435\u0440\u0438\u0442\u043e\u0440\u0456\u0457.\\n\\n\u041e\u0434\u043d\u0430\u043a \u0442\u0430\u043a\u0435 \u0440\u043e\u0437\u0443\u043c\u0456\u043d\u043d\u044f \u043d\u0435 \u0432\u0456\u0434\u043f\u043e\u0432\u0456\u0434\u0430\u0454 \u0431\u0456\u0431\u043b\u0456\u0439\u043d\u043e\u043c\u0443 \u0442\u0435\u043a\u0441\u0442\u0443. \u0426\u0435 \u0441\u043f\u0440\u0430\u0432\u0434\u0456 \u0430\u0431\u0441\u043e\u043b\u044e\u0442\u043d\u043e \u0447\u0443\u0434\u043e\u0432\u0438\u0439 \u0443\u0440\u0438\u0432\u043e\u043a, \u0437 \u043e\u0433\u043b\u044f\u0434\u0443 \u043d\u0430 \u0447\u0430\u0441 \u0439\u043e\u0433\u043e \u043d\u0430\u043f\u0438\u0441\u0430\u043d\u043d\u044f. \u0422\u043e\u0431\u0442\u043e \u043d\u0430\u043f\u0438\u0441\u0430\u043d\u043e \u0446\u0435 \u0431\u0443\u043b\u043e \u0442\u0438\u0441\u044f\u0447\u0456 \u0440\u043e\u043a\u0456\u0432 \u0442\u043e\u043c\u0443, \u0430 \u0441\u044c\u043e\u0433\u043e\u0434\u043d\u0456 \u0432\u0441\u0435 \u0454 \u0442\u0430\u043a, \u044f\u043a \u0431\u0443\u043b\u043e \u043f\u0435\u0440\u0435\u0434\u0431\u0430\u0447\u0435\u043d\u043e.\\n\\n\u041d\u0456, \u043a\u043e\u0440\u0434\u043e\u043d\u0438 \u0406\u0437\u0440\u0430\u0457\u043b\u044e \u043d\u0435 \u043f\u0440\u043e\u0441\u0442\u044f\u0433\u0430\u044e\u0442\u044c\u0441\u044f \u0432\u0456\u0434 \u041d\u0456\u043b\u0443 \u0434\u043e \u0404\u0432\u0444\u0440\u0430\u0442\u0443. \u0410\u0434\u0436\u0435 \u043d\u0430\u0441\u043f\u0440\u0430\u0432\u0434\u0456 \u0446\u0435 \u043d\u0435 \u043f\u0440\u043e \u043a\u043e\u0440\u0434\u043e\u043d\u0438 \u0434\u0435\u0440\u0436\u0430\u0432\u0438 \u0406\u0437\u0440\u0430\u0457\u043b\u044c. \\n\\n\x3c!--truncate--\x3e \\n\\n\u0411\u0443\u043b\u043e \u043d\u0430\u043f\u0438\u0441\u0430\u043d\u043e \u0442\u0430\u043a (\u0411\u0435\u0440\u0435\u0448\u0438\u0442/\u0411\u0443\u0442\u0442\u044f) [15:18](https://mechon-mamre.org/p/pt/pt0115.htm):\\n\\n> \u05d1\u05b7\u05bc\u05d9\u05bc\u05d5\u05b9\u05dd \u05d4\u05b7\u05d4\u05d5\u05bc\u05d0, \u05db\u05b8\u05bc\u05e8\u05b7\u05ea \u05d9\u05b0\u05d4\u05d5\u05b8\u05d4 \u05d0\u05b6\u05ea-\u05d0\u05b7\u05d1\u05b0\u05e8\u05b8\u05dd--\u05d1\u05b0\u05bc\u05e8\u05b4\u05d9\u05ea \u05dc\u05b5\u05d0\u05de\u05b9\u05e8:  \u05dc\u05b0\u05d6\u05b7\u05e8\u05b0\u05e2\u05b2\u05da\u05b8, \u05e0\u05b8\u05ea\u05b7\u05ea\u05b4\u05bc\u05d9 \u05d0\u05b6\u05ea-\u05d4\u05b8\u05d0\u05b8\u05e8\u05b6\u05e5 \u05d4\u05b7\u05d6\u05b9\u05bc\u05d0\u05ea, \u05de\u05b4\u05e0\u05b0\u05bc\u05d4\u05b7\u05e8 \u05de\u05b4\u05e6\u05b0\u05e8\u05b7\u05d9\u05b4\u05dd, \u05e2\u05b7\u05d3-\u05d4\u05b7\u05e0\u05b8\u05bc\u05d4\u05b8\u05e8 \u05d4\u05b7\u05d2\u05b8\u05bc\u05d3\u05b9\u05dc \u05e0\u05b0\u05d4\u05b7\u05e8-\u05e4\u05b0\u05bc\u05e8\u05b8\u05ea.\\n\\n(\u0422\u043e\u0433\u043e \u0434\u043d\u044f \u0413\u043e\u0441\u043f\u043e\u0434\u044c \u0443\u043a\u043b\u0430\u0432 \u0437\u0430\u0432\u0456\u0442 \u0437 \u0410\u0432\u0440\u0430\u043c\u043e\u043c, \u0441\u043a\u0430\u0437\u0430\u0432\u0448\u0438: \\"\u043f\u043e\u0442\u043e\u043c\u0441\u0442\u0432\u0443 \u0442\u0432\u043e\u0454\u043c\u0443 \u0434\u0430\u044e \u042f \u0446\u0456 \u0437\u0435\u043c\u043b\u0456, \u0432\u0456\u0434 \u0440\u0456\u0447\u043a\u0438 \u0404\u0433\u0438\u043f\u0435\u0442\u0441\u044c\u043a\u043e\u0457 \u0434\u043e \u0432\u0435\u043b\u0438\u043a\u043e\u0457 \u0440\u0456\u0447\u043a\u0438, \u0440\u0456\u0447\u043a\u0438 \u0404\u0432\u0444\u0440\u0430\u0442\\")\\n\\n\u0423 \u043f\u0435\u0440\u0435\u043a\u043b\u0430\u0434\u0456 \u041a\u043e\u0440\u043e\u043b\u044f \u042f\u043a\u043e\u0432\u0430:\\n\\n> In that day the LORD made a covenant with Abram, saying: \'Unto thy seed have I given this land, from the river of Egypt unto the great river, the river Euphrates.\'\\n\\n\u041d\u0430\u0449\u0430\u0434\u043a\u0438 \u0410\u0432\u0440\u0430\u0430\u043c\u0430 \u2014 \u0446\u0435 \u043d\u0435 \u043b\u0438\u0448\u0435 \u0454\u0432\u0440\u0435\u0457, \u0430\u043b\u0435 \u0439 \u0430\u0440\u0430\u0431\u0438.\\n\\n\u0422\u043e\u0431\u0442\u043e \u0434\u0456\u0439\u0441\u043d\u043e, \u0443 \u043d\u0430\u0448 \u0447\u0430\u0441, \u044f\u043a \u0456 \u0431\u0443\u043b\u043e \u043f\u0435\u0440\u0435\u0434\u0431\u0430\u0447\u0435\u043d\u043e, \u0437\u0435\u043c\u043b\u044f\u043c\u0438 \u0432\u0456\u0434 \u041d\u0456\u043b\u0443 \u0434\u043e \u0404\u0432\u0444\u0440\u0430\u0442\u0443 \u0432\u043e\u043b\u043e\u0434\u0456\u044e\u0442\u044c \u043d\u0430\u0449\u0430\u0434\u043a\u0438 \u0410\u0432\u0440\u0430\u0430\u043c\u0430.\\n\\n![\\"From the Nile to the Euphrates\\"](/img/Illustrations/blog/2025-02-06-from_the_Nile_to_the_Euphrates/Bereshit_15-18.png) \\n\\n--- \\n\\n\u0414\u0438\u0432. \u0442\u0430\u043a\u043e\u0436: \\n\\n[\u0414\u0435 \u043f\u0440\u043e\u0445\u043e\u0434\u044f\u0442\u044c \u043a\u043e\u0440\u0434\u043e\u043d\u0438 \u0406\u0437\u0440\u0430\u0457\u043b\u044e \u0442\u0430 \u0449\u043e \u043d\u0430\u0441\u043f\u0440\u0430\u0432\u0434\u0456 \u0406\u0437\u0440\u0430\u0457\u043b\u044c \u043e\u043a\u0443\u043f\u0443\u0454](/uk/borders_and_territory_of_israel/)"},{"id":"october7","metadata":{"permalink":"/uk/blog/october7","source":"@site/i18n/uk/docusaurus-plugin-content-blog/2024-10-07-october-7/index.md","title":"7 \u0436\u043e\u0432\u0442\u043d\u044f","description":"\u0426\u0435 \u0442\u0435\u043a\u0441\u0442 \u043c\u043e\u0433\u043e \u0441\u0442\u0430\u0440\u0448\u043e\u0433\u043e \u0441\u0438\u043d\u0430, \u043d\u0430\u043f\u0438\u0441\u0430\u043d\u0438\u0439 \u0447\u0435\u0440\u0435\u0437 \u0442\u0438\u0436\u0434\u0435\u043d\u044c \u043f\u0456\u0441\u043b\u044f 7 \u0436\u043e\u0432\u0442\u043d\u044f. \u0419\u043e\u0433\u043e \u043f\u0456\u0434\u0440\u043e\u0437\u0434\u0456\u043b \u0431\u0443\u0432 \u043e\u0434\u043d\u0438\u043c \u0456\u0437 \u043f\u0435\u0440\u0448\u0438\u0445, \u0445\u0442\u043e \u0432\u0441\u0442\u0443\u043f\u0438\u0432 \u0443 \u0431\u0456\u0439 \u0442\u043e\u0433\u043e \u0440\u0430\u043d\u043a\u0443; \u0432\u043e\u043d\u0438 \u0432\u0456\u0434\u0431\u0438\u043b\u0438 \u0430\u0442\u0430\u043a\u0443 \u0431\u043e\u0439\u043e\u0432\u0438\u043a\u0456\u0432 \u0437\u0456 \u0441\u043f\u0435\u0446\u043f\u0456\u0434\u0440\u043e\u0437\u0434\u0456\u043b\u0443 \\"\u041d\u0443\u0445\u0431\u0430\\" \u043d\u0430 \u0431\u0430\u0437\u0443 \u0423\u0440\u0456\u043c \u043d\u0430 \u043a\u043e\u0440\u0434\u043e\u043d\u0456 \u0437 \u0413\u0430\u0437\u043e\u044e. \u0412\u0456\u043d \u0431\u0443\u0432 \u043f\u043e\u0440\u0430\u043d\u0435\u043d\u0438\u0439 \u0443 \u0442\u043e\u043c\u0443 \u0431\u043e\u044e.","date":"2024-10-07T00:00:00.000Z","tags":[{"inline":false,"label":"\u0412\u0456\u043a\u0442\u043e\u0440 \u0410\u0433\u0454\u0454\u0432","permalink":"/uk/blog/tags/ageyev","description":"\u0412\u0456\u043a\u0442\u043e\u0440 \u0410\u0433\u0454\u0454\u0432"},{"inline":false,"label":"\u0406\u0437\u0440\u0430\u0457\u043b\u044c","permalink":"/uk/blog/tags/israel","description":"\u0406\u0437\u0440\u0430\u0457\u043b\u044c"}],"hasTruncateMarker":true,"authors":[{"name":"Viktor Ageyev","title":"author","page":{"permalink":"/uk/blog/authors/ageyev"},"socials":{"x":"https://x.com/ageyev","linkedin":"https://www.linkedin.com/in/ageyev/","github":"https://github.com/ageyev"},"imageURL":"/uk/img/authors/ageyev_viktor_02.jpg","key":"ageyev"}],"frontMatter":{"slug":"october7","title":"7 \u0436\u043e\u0432\u0442\u043d\u044f","authors":["ageyev"],"tags":["ageyev","israel"],"draft":false},"unlisted":false,"prevItem":{"title":"On the Biblical Borders of Israel","permalink":"/uk/blog/from_the_Nile_to_the_Euphrates"}},"content":"\u0426\u0435 \u0442\u0435\u043a\u0441\u0442 \u043c\u043e\u0433\u043e \u0441\u0442\u0430\u0440\u0448\u043e\u0433\u043e \u0441\u0438\u043d\u0430, \u043d\u0430\u043f\u0438\u0441\u0430\u043d\u0438\u0439 \u0447\u0435\u0440\u0435\u0437 \u0442\u0438\u0436\u0434\u0435\u043d\u044c \u043f\u0456\u0441\u043b\u044f 7 \u0436\u043e\u0432\u0442\u043d\u044f. \u0419\u043e\u0433\u043e \u043f\u0456\u0434\u0440\u043e\u0437\u0434\u0456\u043b \u0431\u0443\u0432 \u043e\u0434\u043d\u0438\u043c \u0456\u0437 \u043f\u0435\u0440\u0448\u0438\u0445, \u0445\u0442\u043e \u0432\u0441\u0442\u0443\u043f\u0438\u0432 \u0443 \u0431\u0456\u0439 \u0442\u043e\u0433\u043e \u0440\u0430\u043d\u043a\u0443; \u0432\u043e\u043d\u0438 \u0432\u0456\u0434\u0431\u0438\u043b\u0438 \u0430\u0442\u0430\u043a\u0443 \u0431\u043e\u0439\u043e\u0432\u0438\u043a\u0456\u0432 \u0437\u0456 \u0441\u043f\u0435\u0446\u043f\u0456\u0434\u0440\u043e\u0437\u0434\u0456\u043b\u0443 \\"\u041d\u0443\u0445\u0431\u0430\\" \u043d\u0430 [\u0431\u0430\u0437\u0443 \u0423\u0440\u0456\u043c](https://he.wikipedia.org/wiki/%D7%94%D7%A7%D7%A8%D7%91_%D7%91%D7%91%D7%A1%D7%99%D7%A1_%D7%90%D7%95%D7%A8%D7%99%D7%9D) \u043d\u0430 \u043a\u043e\u0440\u0434\u043e\u043d\u0456 \u0437 \u0413\u0430\u0437\u043e\u044e. \u0412\u0456\u043d \u0431\u0443\u0432 \u043f\u043e\u0440\u0430\u043d\u0435\u043d\u0438\u0439 \u0443 \u0442\u043e\u043c\u0443 \u0431\u043e\u044e.\\n\\n![Mark](/img/Illustrations/blog/2024-10-07-october-7/2024-08-18.Mark_with_rifle.jpeg)\\n\\n(\u0411\u0430\u0440\u0443\u0445 \u0430\u0428\u0435\u043c, \u0432\u0456\u043d \u043f\u043e\u0432\u0435\u0440\u043d\u0443\u0432\u0441\u044f \u0434\u043e \u0441\u043b\u0443\u0436\u0431\u0438 \u0447\u0435\u0440\u0435\u0437 \u0434\u0432\u0430 \u043c\u0456\u0441\u044f\u0446\u0456 \u0456 \u0437\u0430\u0440\u0430\u0437 \u0432\u043e\u044e\u0454 \u0432 \u0413\u0430\u0437\u0456)\\n\\n[\u0422\u0435\u043a\u0441\u0442 \u0431\u0443\u0432 \u043d\u0430\u043f\u0438\u0441\u0430\u043d\u0438\u0439 \u0456\u0432\u0440\u0456\u0442\u043e\u043c](https://www.instagram.com/p/CybeGarsF_p/), \u043f\u0435\u0440\u0435\u043a\u043b\u0430\u0434 \u043c\u0456\u0439.\\n\\n{/* truncate */}\\n--- \\n\\n\u041f\u0456\u0441\u043b\u044f \u0442\u0438\u0436\u043d\u0435\u0432\u043e\u0457 \u0437\u0430\u0442\u0440\u0438\u043c\u043a\u0438 \u044f \u043d\u0430\u0440\u0435\u0448\u0442\u0456 \u0432\u0438\u0440\u0456\u0448\u0438\u0432 \u043f\u043e\u0434\u0456\u043b\u0438\u0442\u0438\u0441\u044f \u0442\u0438\u043c, \u0449\u043e \u0441\u0442\u0430\u043b\u043e\u0441\u044f \u0437\u0456 \u043c\u043d\u043e\u044e \u0432 \u0434\u0435\u043d\u044c, \u044f\u043a\u0438\u0439 \u044f \u0437\u0430\u043f\u0430\u043c\u2019\u044f\u0442\u0430\u044e \u043d\u0430\u0437\u0430\u0432\u0436\u0434\u0438. \u041c\u043e\u0454\u044e \u0456\u0441\u0442\u043e\u0440\u0456\u0454\u044e, \u0447\u0435\u0440\u0435\u0437 \u044f\u043a\u0443 \u044f \u043f\u0440\u043e\u0439\u0448\u043e\u0432, \u0456 \u0437\u0430\u0432\u0434\u044f\u043a\u0438 \u0432\u0435\u043b\u0438\u043a\u0456\u0439 \u0432\u0434\u0430\u0447\u0456, \u0437\u0430\u0440\u0430\u0437 \u043c\u043e\u0436\u0443 \u043d\u0430\u043f\u0438\u0441\u0430\u0442\u0438 \u0446\u0456 \u0441\u043b\u043e\u0432\u0430.\\n\\n**7 \u0436\u043e\u0432\u0442\u043d\u044f** \u2014 \u0434\u0435\u043d\u044c, \u043a\u043e\u043b\u0438 \u043c\u0438 \u043f\u0440\u043e\u043a\u0438\u043d\u0443\u043b\u0438\u0441\u044f \u0432\u0456\u0434 \u0437\u0432\u0443\u043a\u0456\u0432 \u0441\u0438\u0440\u0435\u043d\u0438 \u043f\u043e \u0432\u0441\u044c\u043e\u043c\u0443 \u0441\u0435\u043a\u0442\u043e\u0440\u0443. \u041c\u0438 \u0431\u0443\u043b\u0438 \u0432 \u0431\u043e\u043c\u0431\u043e\u0441\u0445\u043e\u0432\u0438\u0449\u0456, \u0456 \u043d\u0456\u0445\u0442\u043e \u043d\u0435 \u043d\u0430\u0434\u0430\u0432\u0430\u0432 \u043e\u0441\u043e\u0431\u043b\u0438\u0432\u043e\u0457 \u0443\u0432\u0430\u0433\u0438 \u0446\u0438\u043c \u0440\u0430\u043a\u0435\u0442\u0430\u043c, \u0431\u043e \\"\u0449\u043e \u0432\u043e\u043d\u0438 \u043c\u043e\u0436\u0443\u0442\u044c \u0437\u0440\u043e\u0431\u0438\u0442\u0438, \u043e\u043a\u0440\u0456\u043c \u043a\u0456\u043b\u044c\u043a\u043e\u0445 \u0440\u0430\u043a\u0435\u0442 ...\\"\\n\\n\u041c\u0438 \u043f\u043e\u0447\u0443\u043b\u0438 \u043f\u043e\u0441\u0442\u0440\u0456\u043b\u0438 \u0437 \u0431\u043e\u043a\u0443 \u0441\u0443\u0441\u0456\u0434\u043d\u044c\u043e\u0457 \u0431\u0430\u0437\u0438, \u0456 \u0432\u0436\u0435 \u0447\u0435\u0440\u0435\u0437 \u043a\u0456\u043b\u044c\u043a\u0430 \u0445\u0432\u0438\u043b\u0438\u043d \u043d\u0430 \u043d\u0430\u0441 \u0431\u0443\u043b\u043e \u0441\u043f\u043e\u0440\u044f\u0434\u0436\u0435\u043d\u043d\u044f, \u0456 \u043c\u0438 \u0432\u0438\u0440\u0443\u0448\u0438\u043b\u0438 \u0432 \u0442\u043e\u043c\u0443 \u043d\u0430\u043f\u0440\u044f\u043c\u043a\u0443. \u0417 \u0446\u044c\u043e\u0433\u043e \u043c\u043e\u043c\u0435\u043d\u0442\u0443 \u043c\u0438 \u0432\u0441\u0442\u0443\u043f\u0438\u043b\u0438 \u0443 \u0432\u0456\u0439\u043d\u0443. \u0415\u043c\u043e\u0446\u0456\u0457, \u0449\u043e \u043f\u0435\u0440\u0435\u0436\u0438\u0432\u0430\u0454\u0448 \u0443 \u0446\u0435\u0439 \u0447\u0430\u0441, \u043d\u0435\u0432\u0438\u043c\u043e\u0432\u043d\u0456. \u0422\u0438 \u043f\u0435\u0440\u0435\u0441\u0442\u0430\u0454\u0448 \u0434\u0443\u043c\u0430\u0442\u0438 \u0456 \u0432\u0456\u0434\u0447\u0443\u0432\u0430\u0442\u0438, \u0442\u0438 \u043f\u0440\u043e\u0441\u0442\u043e \u0440\u043e\u0431\u0438\u0448 \u0442\u0435, \u0449\u043e \u043c\u0430\u0454\u0448 \u0440\u043e\u0431\u0438\u0442\u0438. \u0422\u0438 \u043f\u0440\u043e\u0440\u0438\u0432\u0430\u0454\u0448\u0441\u044f, \u043f\u0440\u043e\u0441\u0443\u0432\u0430\u0454\u0448\u0441\u044f \u0432\u043f\u0435\u0440\u0435\u0434 \u0456 \u0441\u0442\u0440\u0456\u043b\u044f\u0454\u0448 \u2014 \u0432\u0441\u0435 \u0446\u0435 \u043d\u0430 \u043d\u0435\u0437\u043d\u0430\u0439\u043e\u043c\u043e\u043c\u0443 \u0442\u043e\u0431\u0456 \u043f\u043e\u043b\u0456 \u0431\u043e\u044e, \u0456 \u0432\u0441\u0435 \u0446\u0435 \u043f\u0456\u0434 \u0441\u0432\u0438\u0441\u0442 \u0440\u0430\u043a\u0435\u0442 \u043d\u0430\u0434 \u0433\u043e\u043b\u043e\u0432\u043e\u044e.\\n\\n\u0406 \u043e\u0441\u044c \u043d\u0430\u0441\u0442\u0430\u0454 \u043c\u043e\u043c\u0435\u043d\u0442, \u043a\u043e\u043b\u0438 \u043c\u0435\u043d\u0435 \u0432\u0440\u0430\u0436\u0430\u0454 \u043a\u0443\u043b\u044f. \u0423 \u0446\u0435\u0439 \u043c\u043e\u043c\u0435\u043d\u0442 \u044f \u043d\u0456\u0447\u043e\u0433\u043e \u043d\u0435 \u0432\u0456\u0434\u0447\u0443\u0432, \u044f \u0434\u0438\u0432\u0438\u0432\u0441\u044f \u043d\u0430 \u0441\u0432\u043e\u044e \u043d\u043e\u0433\u0443 \u0456 \u043d\u0435 \u043c\u0456\u0433 \u043f\u043e\u0432\u0456\u0440\u0438\u0442\u0438, \u0449\u043e \u0446\u0435 \u0432\u0456\u0434\u0431\u0443\u0432\u0430\u0454\u0442\u044c\u0441\u044f. \u0412\u0456\u0434\u0442\u043e\u0434\u0456 \u0432\u0441\u0435 \u0441\u0442\u0430\u043b\u043e\u0441\u044f \u0437\u0430 \u0441\u0435\u043a\u0443\u043d\u0434\u0438. \u0421\u0435\u043a\u0443\u043d\u0434\u0438, \u043a\u043e\u043b\u0438 \u0442\u0435\u0431\u0435 \u0432\u0456\u0434\u0442\u044f\u0433\u0443\u044e\u0442\u044c \u043d\u0430\u0437\u0430\u0434, \u043d\u0430\u043a\u043b\u0430\u0434\u0430\u044e\u0442\u044c \u0434\u0436\u0433\u0443\u0442 \u0456 \u043a\u0430\u0436\u0443\u0442\u044c, \u0449\u043e \u0432\u0441\u0435 \u0431\u0443\u0434\u0435 \u0434\u043e\u0431\u0440\u0435. \u0412\u0441\u0435 \u0441\u0442\u0430\u043b\u043e\u0441\u044f \u0437\u0430 \u0441\u0435\u043a\u0443\u043d\u0434\u0438. \u0421\u0435\u043a\u0443\u043d\u0434\u0438, \u0449\u043e \u0446\u0456\u043b\u0438\u043c\u0438 \u0434\u043d\u044f\u043c\u0438 \u043f\u043e\u0432\u0442\u043e\u0440\u044e\u044e\u0442\u044c\u0441\u044f \u0432 \u0433\u043e\u043b\u043e\u0432\u0456.\\n\\n\u041d\u0435\u043c\u043e\u0436\u043b\u0438\u0432\u043e \u043e\u043f\u0438\u0441\u0430\u0442\u0438 \u043c\u043e\u044e \u0433\u043e\u0440\u0434\u0456\u0441\u0442\u044c \u0437\u0430 \u0442\u043e\u0432\u0430\u0440\u0438\u0448\u0456\u0432 \u0456\u0437 \u043f\u0456\u0434\u0440\u043e\u0437\u0434\u0456\u043b\u0443 414, \u044f\u043a\u0456 \u0431\u0438\u043b\u0438\u0441\u044f \u0456 \u0437\u043d\u0438\u0449\u0438\u043b\u0438 \u0432\u043e\u0440\u043e\u0433\u0430 \u0432 \u0446\u044c\u043e\u043c\u0443 \u0431\u043e\u044e. \u042f \u043b\u044e\u0431\u043b\u044e \u0432\u0430\u0441, \u0456 \u0432\u0438 \u0437\u0430\u0432\u0436\u0434\u0438 \u0431\u0443\u0434\u0435\u0442\u0435 \u0432 \u043c\u043e\u0454\u043c\u0443 \u0441\u0435\u0440\u0446\u0456 \u044f\u043a \u043d\u0430\u0439\u0445\u043e\u0440\u043e\u0431\u0440\u0456\u0448\u0456 \u043b\u044e\u0434\u0438, \u044f\u043a\u0438\u0445 \u044f \u043a\u043e\u043b\u0438-\u043d\u0435\u0431\u0443\u0434\u044c \u0437\u043d\u0430\u0432, \u043a\u043e\u0436\u0435\u043d \u0456 \u043a\u043e\u0436\u043d\u0430 \u0437 \u0432\u0430\u0441. \u0412\u0438 \u2014 \u043c\u043e\u0457 \u0433\u0435\u0440\u043e\u0457 \u0442\u0430 \u0433\u0435\u0440\u043e\u0457\u043d\u0456, \u0456 \u044f \u043f\u0438\u0448\u0430\u044e\u0441\u044f \u0442\u0438\u043c, \u0449\u043e \u0432\u043e\u044e\u0432\u0430\u0432 \u0440\u0430\u0437\u043e\u043c \u0456\u0437 \u0432\u0430\u043c\u0438.\\n\\n\u042f \u043d\u0456\u043a\u043e\u043b\u0438 \u043d\u0435 \u0432\u0456\u0434\u0447\u0443\u0432\u0430\u0432 \u0431\u0456\u043b\u044c\u0448\u043e\u0433\u043e \u0431\u043e\u043b\u044e, \u043d\u0456\u0436 \u0431\u0456\u043b\u044c \u0437\u0430 \u0437\u0430\u0433\u0438\u0431\u043b\u0438\u043c\u0438. \u042f \u0434\u043e\u0441\u0456 \u043d\u0435 \u043c\u043e\u0436\u0443 \u0443\u0441\u0432\u0456\u0434\u043e\u043c\u0438\u0442\u0438 \u043a\u0456\u043b\u044c\u043a\u0456\u0441\u0442\u044c \u043b\u044e\u0434\u0435\u0439, \u044f\u043a\u0438\u0445 \u0437\u0430\u0431\u0440\u0430\u043b\u0438 \u0437 \u0446\u044c\u043e\u0433\u043e \u0441\u0432\u0456\u0442\u0443 \u2014 \u0456 \u0432\u0441\u0435 \u0446\u0435 \u0437\u0430 \u043e\u0434\u0438\u043d \u0434\u0435\u043d\u044c. \u041a\u043e\u0436\u0435\u043d, \u043a\u043e\u0433\u043e \u044f \u0437\u043d\u0430\u044e, \u0432\u0442\u0440\u0430\u0442\u0438\u0432 \u043a\u043e\u0433\u043e\u0441\u044c: \u0442\u043e\u0432\u0430\u0440\u0438\u0448\u0430, \u0440\u043e\u0434\u0438\u0447\u0430, \u0431\u0440\u0430\u0442\u0430 \u0447\u0438 \u0441\u0435\u0441\u0442\u0440\u0443. \u041a\u043e\u0436\u0435\u043d \u0456\u0437 \u043d\u0438\u0445 \u043d\u0430\u0437\u0430\u0432\u0436\u0434\u0438 \u0437\u0430\u043a\u0430\u0440\u0431\u043e\u0432\u0430\u043d\u0438\u0439 \u0443 \u043d\u0430\u0448\u0438\u0445 \u0441\u0435\u0440\u0446\u044f\u0445. \u041d\u0456\u0445\u0442\u043e \u043d\u0435 \u0431\u0443\u0434\u0435 \u0437\u0430\u0431\u0443\u0442\u0438\u0439.\\n\\n*\\"\u0425\u0442\u043e \u043c\u0438\u043b\u043e\u0441\u0435\u0440\u0434\u043d\u0438\u0439 \u0434\u043e \u0436\u043e\u0440\u0441\u0442\u043e\u043a\u0438\u0445, \u0442\u043e\u0439 \u0436\u043e\u0440\u0441\u0442\u043e\u043a\u0438\u0439 \u0434\u043e \u043c\u0438\u043b\u043e\u0441\u0435\u0440\u0434\u043d\u0438\u0445.\\"*\\n\\n--- \\n\\n[\u041f\u0443\u0431\u043b\u0456\u043a\u0430\u0446\u0456\u044f \u043d\u0430 \u0441\u0430\u0439\u0442\u0456 9 \u043a\u0430\u043d\u0430\u043b\u0443 \u0456\u0437\u0440\u0430\u0457\u043b\u044c\u0441\u044c\u043a\u043e\u0433\u043e \u0442\u0435\u043b\u0435\u0431\u0430\u0447\u0435\u043d\u043d\u044f](https://www.9tv.co.il/Item/79640)(\u0440\u043e\u0441\u0456\u0439\u0441\u044c\u043a\u043e\u044e)\\n\\n[\u041f\u0443\u0431\u043b\u0456\u043a\u0430\u0446\u0456\u044f \u043d\u0430 Facebook](https://www.facebook.com/viktor.ageyev/posts/pfbid0AzoFkvcVD9ka8bMTDJDAzg3YKU2asgU6Prg13G3QT8Wsm3nzue477pjx4zUqyC5Xl)(\u0440\u043e\u0441\u0456\u0439\u044c\u043a\u043e\u044e)"}]}}')}}]);